import React, { useState, useRef, useEffect } from 'react';

export default function FileUpload({ label = "파일 선택", inputId }) {
  const [fileName, setFileName] = useState('');
  const [displayName, setDisplayName] = useState('');
  const fileNameRef = useRef(null);

  const truncateMiddle = (str, maxLength = 25) => {
    if (!str) return '';
    if (str.length <= maxLength) return str;
    const keep = Math.floor((maxLength - 3) / 2);
    return str.slice(0, keep) + '...' + str.slice(str.length - keep);
  };

  const handleChange = (e) => {
    const name = e.target.files[0]?.name || '';
    setFileName(name);
  };

  // 파일명 변경 시, span이 넘치면 중간생략
  useEffect(() => {
    if (fileNameRef.current) {
      const el = fileNameRef.current;
      if (el.scrollWidth > el.offsetWidth) {
        setDisplayName(truncateMiddle(fileName, 25));
      } else {
        setDisplayName(fileName);
      }
    }
  }, [fileName]);

  return (
    <div className="fileWrap">
      <input
        type="file"
        id={inputId}
        className="fileInput"
        onChange={handleChange}
      />
      <label htmlFor={inputId} className="fileLabel">
        {label}
      </label>
      <span className="fileName" ref={fileNameRef}>
        {displayName}
      </span>
    </div>
  );
}
