import { useState } from 'react';

function FileUpload({ label = "파일 선택", onFileChange }) {
  const [fileName, setFileName] = useState(label);

  const handleChange = (e) => {
    if (e.target.files.length > 0) {
      setFileName(e.target.files[0].name);
      if (onFileChange) onFileChange(e.target.files);
    } else {
      setFileName(label);
    }
  };

  return (
    <div className="fileWrap">
      <input
        type="file"
        id={`fileInput-${label}`} // 여러 컴포넌트 동시 사용 가능
        className="fileInput"
        onChange={handleChange}
      />
      <label htmlFor={`fileInput-${label}`} className="fileLabel">
        {fileName}
      </label>
    </div>
  );
}

export default FileUpload;
