import { useRef, useState, useEffect } from 'react';

export default function Slider({ value = 100, onChange }) {
  const barRef = useRef(null);
  const [percent, setPercent] = useState(value);

  const update = (x) => {
    const rect = barRef.current.getBoundingClientRect();
    let v = ((x - rect.left) / rect.width) * 100;
    v = Math.max(0, Math.min(100, v));
    setPercent(v);
    onChange?.(Math.round(v));
  };

  const onDown = (e) => {
    update(e.clientX);

    const move = (ev) => update(ev.clientX);
    const up = () => {
      document.removeEventListener('mousemove', move);
      document.removeEventListener('mouseup', up);
    };

    document.addEventListener('mousemove', move);
    document.addEventListener('mouseup', up);
  };

  useEffect(() => setPercent(value), [value]);

  return (
    <div className="sliderControl">
      <div className="sliderBar" ref={barRef} onMouseDown={onDown}>
        <div className="sliderBarFill" style={{ width: `${percent}%` }} />
        <div className="sliderHandle" style={{ left: `${percent}%` }} />
      </div>
    </div>
  );
}
