import React, { useState, useEffect } from 'react';

const NumberInput = ({ 
    initialValue = 0, 
    min = -Infinity, 
    max = Infinity, 
    step = 1, 
    placeholder = "최소",
    onChange 
}) => {
    const [value, setValue] = useState(initialValue);

    // 내부 상태 변경 시 부모에게 전달
    useEffect(() => {
        if (onChange) onChange(value);
    }, [value, onChange]);

    const handleUpdate = (type) => {
        setValue((prev) => {
            const numPrev = prev === '' ? 0 : Number(prev);
            let newValue = type === 'up' ? numPrev + step : numPrev - step;

            // 최소/최대값 제한
            return Math.min(Math.max(newValue, min), max);
        });
    };

    const handleChange = (e) => {
        const val = e.target.value;
        if (val === '') {
            setValue('');
            return;
        }

        const numVal = Number(val);
        if (!isNaN(numVal)) {
            setValue(Math.min(Math.max(numVal, min), max));
        }
    };

    const handleKeyDown = (e) => {
        if (e.key === 'ArrowUp') {
            e.preventDefault();
            handleUpdate('up');
        } else if (e.key === 'ArrowDown') {
            e.preventDefault();
            handleUpdate('down');
        }
    };

    return (
        <div className="numInput">
            <input 
                type="number" 
                placeholder={placeholder} 
                value={value}
                onChange={handleChange}
                onKeyDown={handleKeyDown}
                min={min}
                max={max}
                step={step}
            />
            <div className="spin">
                <button 
                    type="button" 
                    className="spinUp" 
                    onClick={() => handleUpdate('up')}
                >
                    <span className="blind">증가</span>
                </button>
                <button 
                    type="button" 
                    className="spinDown" 
                    onClick={() => handleUpdate('down')}
                >
                    <span className="blind">감소</span>
                </button>
            </div>
        </div>
    );
};

export default NumberInput;
