import { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";

import NavComponent from "./side/NavComponent";

import Panel1Component from "./side/Panel1Component";
import Panel2Component from "./side/Panel2Component";
import Panel3Component from "./side/Panel3Component";
import Panel4Component from "./side/Panel4Component";
import Panel5Component from "./side/Panel5Component";
import Panel6Component from "./side/Panel6Component";
import Panel7Component from "./side/Panel7Component";
import Panel8Component from "./side/Panel8Component";
import DisplayComponent from "./side/DisplayComponent";

export default function SideComponent() {
  const navigate = useNavigate();
  const location = useLocation();

  /* =========================
     패널 열림 상태
  ========================= */
  const [isPanelOpen, setIsPanelOpen] = useState(true);
  const handleTogglePanel = () => setIsPanelOpen(prev => !prev);

  /* =========================
     URL → activeKey
  ========================= */
  const getActiveKey = () => {
    const path = location.pathname;

    if (path.startsWith("/ship")) return "gnb1";
    if (path.startsWith("/satellite")) return "gnb2";
    if (path.startsWith("/weather")) return "gnb3";
    if (path.startsWith("/analysis")) return "gnb4";
    if (path.startsWith("/timeline")) return "gnb5";
    if (path.startsWith("/signal")) return "gnb6";
    if (path.startsWith("/replay")) return "gnb7";
    if (path.startsWith("/track")) return "gnb8";
    if (path.startsWith("/layer")) return "layer";
    if (path.startsWith("/filter")) return "filter";

    return "gnb1";
  };

  const activeKey = getActiveKey();

  /* =========================
     네비 클릭 → 절대경로
  ========================= */
  const handleChangePanel = (key) => {
    setIsPanelOpen(true);

    switch (key) {
      case "gnb1": navigate("/ship"); break;
      case "gnb2": navigate("/satellite/add"); break;
      case "gnb3": navigate("/weather"); break;
      case "gnb4": navigate("/analysis/area"); break;
      case "gnb5": navigate("/timeline"); break;
      case "gnb6": navigate("/signal"); break;
      case "gnb7": navigate("/replay"); break;
      case "gnb8": navigate("/track"); break;
      case "layer": navigate("/layer/register"); break;
      case "filter": navigate("/filter"); break;
      default: navigate("/ship"); break;
    }
  };

  /* =========================
     공통 패널 props
  ========================= */
  const panelProps = {
    isOpen: isPanelOpen,
    onToggle: handleTogglePanel,
  };

  return (
    <section id="sidePanel">
      <NavComponent activeKey={activeKey} onChange={handleChangePanel} />

      <div className="sidePanelContent">
        {activeKey === "gnb1" && <Panel1Component {...panelProps} />}
        {activeKey === "gnb2" && <Panel2Component {...panelProps} />}
        {activeKey === "gnb3" && <Panel3Component {...panelProps} />}
        {activeKey === "gnb4" && <Panel4Component {...panelProps} />}
        {activeKey === "gnb5" && <Panel5Component {...panelProps} />}
        {activeKey === "gnb6" && <Panel6Component {...panelProps} />}
        {activeKey === "gnb7" && <Panel7Component {...panelProps} />}
        {activeKey === "gnb8" && <Panel8Component {...panelProps} />}
        {(activeKey === "layer" || activeKey === "filter") && (
          <DisplayComponent {...panelProps} />
        )}
      </div>
    </section>
  );
}
