import { useCallback, useId } from "react";

export default function SpinNumberInput({
  value,
  onChange,
  min = -Infinity,
  max = Infinity,
  step = 1,
  placeholder = "",
  label = "",
}) {
  const id = useId();

  const clamp = (val) => Math.min(Math.max(val, min), max);

  const handleInputChange = (e) => {
    const v = e.target.value;
    onChange(v === "" ? "" : clamp(Number(v)));
  };

  const increase = useCallback(() => {
    const next = value === "" ? step : Number(value) + step;
    onChange(clamp(next));
  }, [value, step, min, max]);

  const decrease = useCallback(() => {
    const next = value === "" ? 0 : Number(value) - step;
    onChange(clamp(next));
  }, [value, step, min, max]);

  const handleKeyDown = (e) => {
    if (e.key === "ArrowUp") {
      e.preventDefault();
      increase();
    }
    if (e.key === "ArrowDown") {
      e.preventDefault();
      decrease();
    }
  };

  return (
    <label htmlFor={id}>
      {/* 화면에 안 보이는 라벨 */}
      <span className="blind">{label}</span>

      <div className="numInput">
        <input
          id={id}
          type="number"
          value={value}
          min={min}
          max={max}
          step={step}
          placeholder={placeholder}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
        />

        <div className="spin">
          <button
            type="button"
            className="spinUp"
            onClick={increase}
            aria-label={`${label} 증가`}
          >
            <span className="blind">증가</span>
          </button>

          <button
            type="button"
            className="spinDown"
            onClick={decrease}
            aria-label={`${label} 감소`}
          >
            <span className="blind">감소</span>
          </button>
        </div>
      </div>
    </label>
  );
}
